[ { "id": 1544205542000, "trigger": 1, "priority": 0, "title": "Clear cache", "description": "Clears all cache directories from internal storage and protected sdcard storage. Needs root.", "content": "# Clear cache\n# Clears all cache directories from internal storage and protected sdcard storage. \n# Needs root.\nrm -rf \/sdcard\/.thumbnails\nfind \/data\/data\/ -type d -name \"cache\" -exec rm -rf \"{}\" \\; \nfind \/sdcard\/Android\/ -type d -name \"cache\" -exec rm -rf \"{}\" \\; \n ", "as_root": true, "show_notification": true, "show_result": false, "result_in_file": false }, { "id": 1544205542001, "trigger": 0, "priority": 0, "title": "Clear temp files", "description": "Clears all files with tmp, temp and cache extension. Needs root.", "content": "# Clear temp files\n# Clears all files with tmp, temp and cache extension. Needs root.\n# Needs root.\nfind \/data\/data\/ -type f -name \".tmp\" -delete \nfind \/data\/data\/ -type f -name \".temp\" -delete \nfind \/data\/data\/ -type f -name \".cache\" -delete \nfind \/sdcard\/ -type f -name \".tmp\" -delete \nfind \/sdcard\/ -type f -name \".temp\" -delete \nfind \/sdcard\/ -type f -name \".cache\" -delete \n ", "as_root": true, "show_notification": true, "show_result": true, "result_in_file": false }, { "id": 1544205542002, "trigger": 0, "priority": 0, "title": "Reboot in recovery", "description": "Reboots device in recovery mode.", "content": "# Reboot in recovery mode\n# Reboots device in recovery mode.\nsvc power reboot recovery\n ", "as_root": true, "show_notification": false, "show_result": true, "result_in_file": false }, { "id": 1544205542003, "trigger": 0, "priority": 0, "title": "Reboot device", "description": "Reboots device.", "content": "# Reboot device\n# Reboots devices.\nsvc power reboot\n ", "as_root": true, "show_notification": false, "show_result": true, "result_in_file": false } ]
